import React from "react";

export default props => {

    const valoresMega = [];
    let valorAleatorio = 0;

    gerarNumero(props.quantidade)
    {
        for(let i = 0; i < props.quantidade; i++)
        {
            valorAleatorio = Math.floor(Math.random() * (60 - 1)) + 1;
            if(valoresMega.length == 0)
            {
                valoresMega.push(valorAleatorio);
            }
            
            if(valoresMega.find(e => e == valorAleatorio))
            {
                while(valoresMega.find(e => e == valorAleatorio))
                {
                    valorAleatorio = Math.floor(Math.random() * (60 - 1)) + 1; 
                }
            }
            
            valoresMega.push(valorAleatorio);
        }

        valoresMega.sort();
    }

    return (
        <button>Gera Numeros</button>
    )
}
