import React, {useState} from "react";
import './input.css'

export default props => {
    const [valor, setValor] = useState('Inicial');

    function quandoMudar(e) {
        setValor(e.target.value)
    }

    return(
        <div>
            <h2>{valor}</h2>
            <input value={valor} onChange={quandoMudar}  className="input"/>
        </div>
    )
}
