import React from "react";

export default class Contador extends React.Component
{

    constructor(props)
    {
        super(props);
        state = {
            numero: props.numeroInicial
        }
    }

    render()
    {
        return (
            <div>
                <h2>Contador</h2>
                <p>{this.state.numero}</p> 
            </div>
        )
    }   
}
