import React from "react";

export default class FuncaoNumero extends React.Component {

    state = {
        valoresMega: [],
        valorAleatorio: 0,
    }

    gerarNumero = () =>
    {
        for(let i = 0; i < 10; i++)
        {
            this.state.valorAleatorio = Math.floor(Math.random() * (60 - 1)) + 1;
            if(this.state.valoresMega.length == 0)
            {
                this.state.valoresMega.push(valorAleatorio);
            }
            
            if(this.state.valoresMega.find(e => e == this.state.valorAleatorio))
            {
                while(this.state.valoresMega.find(e => e == this.state.valorAleatorio))
                {
                    this.state.valorAleatorio = Math.floor(Math.random() * (60 - 1)) + 1; 
                }
            }
            
            this.state.valoresMega.push(this.state.valorAleatorio);
        }

        this.state.valoresMega.sort();
    }

    render() {
        return (
            <ul>
                { this.state.valoresMega.map((valor) => <li>{valor} , </li>) }
            </ul>
        )
    }
}
