import React from "react";

export default class Contador extends React.Component
{

    state = {
        numero: this.props.numeroInicial
    }

    incrementar()
    {
        this.setState({
            numero: this.state.numero++
        });
    }

    render()
    {
        return (
            <div>
                <h2>Contador</h2>
                <p>{this.state.numero}</p> 
                <button onClick={incrementar()}>Incrementar</button>
            </div>
        )
    }   
}
